function renderImagePreview(e){if(!(e.files.length<1)){document.getElementById("cover-upload-button").innerHTML=e.files[0].name;var t=document.getElementById("song-cover");t.src=window.URL.createObjectURL(e.files[0])}}function selectCover(){document.querySelector(".file-input").click()}function confirmSubmit(e){return!!confirm("Are you sure you want to delete this song?")||(e.preventDefault(),!1)}document.querySelector(".song-image-container").addEventListener("click",selectCover),document.getElementById("delete-button").addEventListener("click",confirmSubmit);